class My_Resumes extends StatefulWidget {
  static const String idScreen = "MyResumes";
  final String resumePath;
  const My_Resumes({required this.resumePath});

  @override
  _My_ResumesState createState() => _My_ResumesState();
}

class _My_ResumesState extends State<My_Resumes> {
  bool isload = true;
  @override
  void initState() {
    // setDocName();
    print(widget.resumePath);
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
        endDrawer: (userDetailsResponse["user_type"] == 'user' ||
                userDetailsResponse["user_type"] == null)
            ? Sidemenu()
            : SidemenuEmployer(),
        appBar: AppBar(
          iconTheme: IconThemeData(color: Color(0xffdd312d)),
          elevation: 0,
          titleSpacing: 0,
          leading: GestureDetector(
            onTap: () {
              Navigator.pop(context);
            },
            child: Icon(
              Icons.arrow_back_ios,
              color: Colors.black,
            ),
          ),
          title: Text(
            "Profile",
            style: TextStyle(
              fontFamily: 'Poppins',
              fontSize: 18,
              color: const Color(0xff1d1d1d),
              fontWeight: FontWeight.w500,
            ),
            textAlign: TextAlign.center,
          ),
          backgroundColor: const Color(0xfff7f9fc),
          centerTitle: true,
        ),
        backgroundColor: const Color(0xfff7f9fc),
        body: Container(
          constraints: BoxConstraints(
              minHeight: MediaQuery.of(context).size.height,
              minWidth: MediaQuery.of(context).size.width),
          child: _showView(),
          // children: [
          //   _showView(),
          //   // _showView()
          // ],
        ));
  }

  Widget _showView() {
    if (widget.resumePath.endsWith("pdf")) {
      return Container(
        child: SfPdfViewer.network(
          widget.resumePath,
        ),
      );
    } else {
      return Center(
        child: SingleChildScrollView(
          child: Container(
            color: Color(0xfff7f9fc),
            child: Image(
              image: NetworkImage(widget.resumePath),
              width: MediaQuery.of(context).size.width - 40,
            ),
            // margin: EdgeInsets.only(top: 10),
            // child: Image.network(
            //   resumePath,
            //   width: MediaQuery.of(context).size.width - 40,
            // ),
            // width: MediaQuery.of(context).size.width - 40,
          ),
        ),
      );
    }
  }
}

